<?php class clqarray{public static function toString($array){$result=array();$depth=0;foreach($array as$k=>$v){$show_val=(is_array($v)?"":$v);$result[]=str_repeat("  ",$depth);if($depth==0){$result[]="O ";}elseif(is_array($v)){$result[]="+ ";}else{$result[]="- ";}if($show_val==""){$result[]="<strong>{$k}</strong>:";}else{$result[]=$k." (".$show_val.")"."";}if(is_array($v)){$temp=self::toTree($v,($depth+1));foreach($temp as$t){$result[]=$t;}}}return implode($result);}private static function showtype($show_val){if(is_string($show_val)&&($show_val=="true"||$show_val=="false")){return"\"{$show_val}\"";}elseif(is_bool($show_val)&&$show_val===true){return"true";}elseif(is_bool($show_val)&&$show_val===false){return"false";}elseif(is_null($show_val)){return"null";}else{return$show_val;}}private static function toTree($pieces,$depth=0){foreach($pieces as$k=>$v){$show_val=(is_array($v)?"":$v);$show_val=self::showtype($show_val);$result[]=str_repeat("  ",$depth);if($depth==0){$result[]="O ";}elseif(is_array($v)){$result[]="+ ";}else{$result[]="- ";}if($show_val==""){$result[]="<strong>{$k}</strong>:";}else{$result[]=$k.": <i>{$show_val}</i>";}if(is_array($v)){$temp=self::toTree($v,($depth+1));if(is_array($temp)){foreach($temp as$t){$result[]=$t;}}else{$result[]=$t;}}}return$result;}}?>